react.component:
import React, { useCallback, useEffect, useId, useRef, useState } from "react";
import clsx from "clsx";

export interface TabItem {
  id: string;
  label: string;
  content: React.ReactNode;
  disabled?: boolean;
}

export interface TabsProps {
  /** Array of tab items */
  tabs: TabItem[];
  /** Initially selected tab ID */
  defaultSelectedId?: string;
  /** Orientation of the tabs */
  orientation?: "horizontal" | "vertical";
  /** Activation mode */
  activation?: "automatic" | "manual";
  /** Whether tabs can be deleted */
  deletable?: boolean;
  /** Callback when tab selection changes */
  onSelectionChange?: (tabId: string) => void;
  /** Callback when tab is deleted */
  onTabDelete?: (tabId: string) => void;
  /** Additional CSS class */
  className?: string;
}

const Tabs = React.memo(function Tabs({
  tabs,
  defaultSelectedId,
  orientation = "horizontal",
  activation = "automatic",
  deletable = false,
  onSelectionChange,
  onTabDelete,
  className,
}: TabsProps): JSX.Element {
  // Find initial tab or use first available tab
  const initialTab = defaultSelectedId 
    ? tabs.find(tab => tab.id === defaultSelectedId && !tab.disabled)
    : tabs.find(tab => !tab.disabled);

  const [selectedId, setSelectedId] = useState(initialTab?.id || tabs[0]?.id);
  const [focusedIndex, setFocusedIndex] = useState(0);

  const tablistRef = useRef<HTMLDivElement>(null);
  const tabRefs = useRef<Record<string, HTMLButtonElement | null>>({});

  // Generate unique IDs for accessibility
  const tablistId = useId();

  // Get available (non-disabled) tabs
  const availableTabs = tabs.filter(tab => !tab.disabled);

  const handleTabSelection = useCallback((tabId: string) => {
    setSelectedId(tabId);
    onSelectionChange?.(tabId);
  }, [onSelectionChange]);

  const handleTabFocus = useCallback((index: number) => {
    setFocusedIndex(index);
    const tab = availableTabs[index];
    if (tab && tabRefs.current[tab.id]) {
      tabRefs.current[tab.id]?.focus();
    }
  }, [availableTabs]);

  const handleKeyDown = useCallback((event: React.KeyboardEvent) => {
    const { key } = event;

    // Only handle keyboard events if focus is on a tab
    const target = event.target;
    if (!tablistRef.current || !(target instanceof Node) || !tablistRef.current.contains(target)) {
      return;
    }

    let newIndex = focusedIndex;
    let shouldPreventDefault = false;

    switch (key) {
      case "ArrowRight":
      case "ArrowDown":
        newIndex = orientation === "horizontal" 
          ? (focusedIndex + 1) % availableTabs.length
          : (focusedIndex + 1) % availableTabs.length;
        shouldPreventDefault = true;
        break;

      case "ArrowLeft":
      case "ArrowUp":
        newIndex = orientation === "horizontal"
          ? (focusedIndex - 1 + availableTabs.length) % availableTabs.length
          : (focusedIndex - 1 + availableTabs.length) % availableTabs.length;
        shouldPreventDefault = true;
        break;

      case "Home":
        newIndex = 0;
        shouldPreventDefault = true;
        break;

      case "End":
        newIndex = availableTabs.length - 1;
        shouldPreventDefault = true;
        break;

      case "Enter":
      case " ":
        if (activation === "manual") {
          const focusedTab = availableTabs[focusedIndex];
          if (focusedTab) {
            handleTabSelection(focusedTab.id);
          }
        }
        shouldPreventDefault = true;
        break;

      case "Delete":
        if (deletable) {
          const focusedTab = availableTabs[focusedIndex];
          if (focusedTab) {
            onTabDelete?.(focusedTab.id);
          }
        }
        shouldPreventDefault = true;
        break;
    }

    if (shouldPreventDefault) {
      event.preventDefault();

      if (newIndex !== focusedIndex) {
        handleTabFocus(newIndex);

        // Automatic activation: select tab when focus moves
        if (activation === "automatic") {
          const newTab = availableTabs[newIndex];
          if (newTab) {
            handleTabSelection(newTab.id);
          }
        }
      }
    }
  }, [
    focusedIndex,
    availableTabs,
    orientation,
    activation,
    deletable,
    handleTabSelection,
    handleTabFocus,
    onTabDelete
  ]);

  // Update focused index when selected tab changes
  useEffect(() => {
    const selectedIndex = availableTabs.findIndex(tab => tab.id === selectedId);
    if (selectedIndex >= 0) {
      setFocusedIndex(selectedIndex);
    }
  }, [selectedId, availableTabs]);

  return (
    <div className={clsx(
      "apg-tabs",
      {
        "apg-tabs--horizontal": orientation === "horizontal",
        "apg-tabs--vertical": orientation === "vertical"
      },
      className
    )}>
      {/* Tablist */}
      <div
        ref={tablistRef}
        role="tablist"
        aria-orientation={orientation}
        className={clsx(
          "apg-tablist",
          {
            "apg-tablist--horizontal": orientation === "horizontal",
            "apg-tablist--vertical": orientation === "vertical"
          }
        )}
        onKeyDown={handleKeyDown}
      >
        {tabs.map((tab) => {
          const isSelected = tab.id === selectedId;
          const tabIndex = tab.disabled ? -1 : (isSelected ? 0 : -1);
          const tabPanelId = `${tablistId}-panel-${tab.id}`;

          return (
            <button
              key={tab.id}
              ref={(el) => {
                tabRefs.current[tab.id] = el;
              }}
              role="tab"
              type="button"
              id={`${tablistId}-tab-${tab.id}`}
              aria-selected={isSelected}
              aria-controls={isSelected ? tabPanelId : undefined}
              tabIndex={tabIndex}
              disabled={tab.disabled}
              className={clsx(
                "apg-tab",
                {
                  "apg-tab--horizontal": orientation === "horizontal",
                  "apg-tab--vertical": orientation === "vertical",
                  "apg-tab--selected": isSelected,
                  "apg-tab--disabled": tab.disabled
                }
              )}
              onClick={() => !tab.disabled && handleTabSelection(tab.id)}
            >
              <span className="apg-tab-label">{tab.label}</span>
              {deletable && !tab.disabled && (
                <button
                  type="button"
                  className="apg-tab-delete"
                  aria-label={`Delete ${tab.label} tab`}
                  onClick={(e) => {
                    e.stopPropagation();
                    onTabDelete?.(tab.id);
                  }}
                >
                  ×
                </button>
              )}
            </button>
          );
        })}
      </div>

      {/* Tab Panels */}
      <div className="apg-tabpanels">
        {tabs.map((tab) => {
          const isSelected = tab.id === selectedId;
          const tabPanelId = `${tablistId}-panel-${tab.id}`;

          return (
            <div
              key={tab.id}
              role="tabpanel"
              id={tabPanelId}
              aria-labelledby={`${tablistId}-tab-${tab.id}`}
              hidden={!isSelected}
              className={clsx(
                "apg-tabpanel",
                {
                  "apg-tabpanel--active": isSelected,
                  "apg-tabpanel--inactive": !isSelected
                }
              )}
              tabIndex={isSelected ? 0 : -1}
            >
              {tab.content}
            </div>
          );
        })}
      </div>
    </div>
  );
});

export default Tabs;

shared.styles:
/* =============================================================================
   TABS COMPONENT STYLES
   ============================================================================= */

@layer components {
  /* Tabs Container */
  .apg-tabs {
    @apply flex border border-gray-300 rounded-md overflow-hidden;
  }

  .apg-tabs--vertical {
    @apply flex-row;
  }

  .apg-tabs--horizontal {
    @apply flex-col;
  }

  /* Responsive: vertical becomes horizontal on mobile */
  @media (max-width: 768px) {
    .apg-tabs--vertical {
      @apply flex-col;
    }
  }

  /* Tablist */
  .apg-tablist {
    @apply flex bg-gray-50 overflow-x-auto;
  }

  .apg-tablist--horizontal {
    @apply flex-row border-b border-gray-300;
  }

  .apg-tablist--vertical {
    @apply flex-col border-r border-gray-300 min-w-[200px];
  }

  /* Responsive: vertical tablist becomes horizontal on mobile */
  @media (max-width: 768px) {
    .apg-tablist--vertical {
      @apply flex-row border-r-0 border-b border-gray-300 min-w-0 overflow-x-auto;
    }
  }

  /* Individual Tab */
  .apg-tab {
    @apply relative flex items-center gap-2 bg-transparent border-0 px-4 py-3;
    @apply text-sm font-medium text-gray-500 cursor-pointer whitespace-nowrap;
    @apply transition-all duration-200 ease-in-out;
    @apply focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-inset focus:z-10;
    @apply min-h-[44px]; /* Touch target minimum size */
  }

  .apg-tab--horizontal {
    @apply border-b-2 border-transparent;
  }

  .apg-tab--vertical {
    @apply border-r-2 border-transparent justify-start w-full text-left;
    @apply flex-shrink-0 min-w-[120px];
  }

  /* Responsive: vertical tabs become horizontal on mobile */
  @media (max-width: 768px) {
    .apg-tab--vertical {
      @apply border-r-0 border-b-2 border-transparent;
      @apply justify-center text-center min-w-[120px] w-auto;
    }
  }

  /* Tab States */
  .apg-tab:hover:not(.apg-tab--disabled) {
    @apply bg-gray-100 text-gray-700;
  }

  .apg-tab--selected.apg-tab--horizontal {
    @apply bg-white text-gray-700 border-b-blue-500;
  }

  .apg-tab--selected.apg-tab--vertical {
    @apply bg-white text-gray-700 border-r-blue-500;
  }

  /* Responsive: selected vertical tabs on mobile */
  @media (max-width: 768px) {
    .apg-tab--selected.apg-tab--vertical {
      @apply border-r-transparent border-b-blue-500;
    }
  }

  .apg-tab--disabled {
    @apply text-gray-400 cursor-not-allowed opacity-60;
  }

  .apg-tab--disabled:hover {
    @apply bg-transparent text-gray-400;
  }

  /* Tab Label */
  .apg-tab-label {
    @apply flex-1;
  }

  /* Delete Button */
  .apg-tab-delete {
    @apply flex items-center justify-center w-5 h-5 bg-transparent border-0 rounded-full;
    @apply text-base leading-none text-gray-500 cursor-pointer ml-1;
    @apply transition-all duration-200 ease-in-out;
    @apply hover:bg-red-500 hover:text-white;
    @apply focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-1;
  }

  /* Tab Panels Container */
  .apg-tabpanels {
    @apply flex-1 relative bg-white;
  }

  /* Individual Tab Panel */
  .apg-tabpanel {
    @apply p-4 min-h-[200px];
    @apply focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-inset;
  }

  .apg-tabpanel--active {
    @apply block;
  }

  .apg-tabpanel--inactive {
    @apply hidden;
  }
}

/* =============================================================================
   ACCESSIBILITY ENHANCEMENTS
   ============================================================================= */

@layer utilities {
  /* High Contrast Mode Support */
  @media (prefers-contrast: high) {
    .apg-tabs {
      @apply border-4;
    }

    .apg-tab--selected {
      @apply bg-black text-white;
    }

    .apg-tab--selected.apg-tab--horizontal {
      @apply border-b-yellow-400;
    }

    .apg-tab--selected.apg-tab--vertical {
      @apply border-r-yellow-400;
    }

    .apg-tab:not(.apg-tab--selected):not(.apg-tab--disabled) {
      @apply bg-white text-black border-black;
    }
  }

  /* Reduced Motion Support */
  @media (prefers-reduced-motion: reduce) {
    .apg-tab,
    .apg-tab-delete {
      @apply transition-none;
    }
  }

  /* Focus Enhancement for Keyboard Users */
  .apg-tab:focus-visible {
    @apply ring-2 ring-blue-500 ring-offset-2;
    @apply outline outline-2 outline-blue-600 outline-offset-2;
  }

  /* Windows High Contrast Mode */
  @media (forced-colors: active) {
    .apg-tabs {
      border: 2px solid ButtonBorder;
      background: ButtonFace;
    }

    .apg-tablist {
      background: ButtonFace;
      border-color: ButtonBorder;
    }

    .apg-tab {
      border-color: ButtonBorder;
      background: ButtonFace;
      color: ButtonText;
    }

    .apg-tab--selected {
      background: Highlight;
      color: HighlightText;
      border-color: Highlight;
    }

    .apg-tab:focus {
      outline: 2px solid Highlight;
    }

    .apg-tabpanels {
      background: ButtonFace;
    }

    .apg-tabpanel {
      color: ButtonText;
    }
  }

  /* Mobile-specific optimizations */
  @media (max-width: 768px) {
    .apg-tab {
      @apply flex-shrink-0;
    }
  }
}
